DListNode* DListInit(DListNode* phead)
{
	phead = (DListNode*)malloc(sizeof(DListNode));
	phead->next = phead;
	phead->prev = phead;
	return phead;
}

void DListPushBack(DListNode* phead, DLTDataType n)
{
	assert(phead);
	DListNode* tail = phead->prev;
	DListNode* newNode = (DListNode*)malloc(sizeof(DListNode));
	newNode->data = n;
	newNode->next = phead;
	tail->next = newNode;
	newNode->prev = tail;
	phead->prev = newNode;
}

void DListPopBack(DListNode* phead, DLTDataType n)
{
	assert(phead);
	if (phead->next == phead)
	{
		return;
	}
	DListNode* tailPrev = phead->prev->prev;
	tailPrev->next = phead;
	free(phead->prev);
	phead->prev = tailPrev;
}

void DListNodePrint(DListNode* phead)
{
	assert(phead);
	
	DListNode* cur = phead->next;
	while (cur != phead)
	{
		printf("%d->", cur->data);
		cur = cur->next;
	}
	printf("NULL\n");
}

